import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:3001/api';

function Exams({ password }) {
  const [exams, setExams] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newExam, setNewExam] = useState({ subject: '', exam_date: '', description: '' });

  useEffect(() => {
    const fetchExams = async () => {
      const res = await fetch(`${API_URL}/exams`, { headers: { password } });
      setExams(await res.json());
    };
    fetchExams();
  }, [password]);

  const addExam = async (e) => {
    e.preventDefault();
    await fetch(`${API_URL}/exams`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', password },
      body: JSON.stringify(newExam)
    });
    setNewExam({ subject: '', exam_date: '', description: '' });
    setShowForm(false);
    loadExams();
  };

  const deleteExam = async (id) => {
    if (window.confirm('Remover esta prova?')) {
      await fetch(`${API_URL}/exams/${id}`, {
        method: 'DELETE',
        headers: { password }
      });
      loadExams();
    }
  };

  const getDaysUntil = (dateStr) => {
    const today = new Date();
    const examDate = new Date(dateStr);
    const diffTime = examDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  const upcoming = exams.filter(exam => getDaysUntil(exam.exam_date) >= 0);
  const past = exams.filter(exam => getDaysUntil(exam.exam_date) < 0);

  return (
    <div>
      <div className="card">
        <div className="card-header">
          <h3>🎓 Próximas Provas</h3>
          <button className="btn btn-primary" onClick={() => setShowForm(!showForm)}>
            + Adicionar Prova
          </button>
        </div>

        {showForm && (
          <form onSubmit={addExam} style={{ marginBottom: '20px' }}>
            <input
              type="text"
              placeholder="Matéria"
              value={newExam.subject}
              onChange={(e) => setNewExam({ ...newExam, subject: e.target.value })}
              required
            />
            <input
              type="date"
              value={newExam.exam_date}
              onChange={(e) => setNewExam({ ...newExam, exam_date: e.target.value })}
              required
            />
            <textarea
              placeholder="Descrição (opcional)"
              value={newExam.description}
              onChange={(e) => setNewExam({ ...newExam, description: e.target.value })}
              rows="3"
            />
            <button type="submit" className="btn btn-primary">Salvar</button>
          </form>
        )}

        {upcoming.length === 0 ? (
          <p>Nenhuma prova agendada</p>
        ) : (
          <div>
            {upcoming.map(exam => {
              const daysUntil = getDaysUntil(exam.exam_date);
              return (
                <div key={exam.id} className="item" style={{ marginBottom: '10px' }}>
                  <div className="item-header">
                    <div>
                      <h4>{exam.subject}</h4>
                      <p style={{ margin: '5px 0' }}>
                        📅 {new Date(exam.exam_date).toLocaleDateString('pt-BR')}
                        {daysUntil === 0 && ' (HOJE!)'}
                        {daysUntil === 1 && ' (AMANHÃ!)'}
                        {daysUntil > 1 && ` (em ${daysUntil} dias)`}
                      </p>
                      {exam.description && <small>{exam.description}</small>}
                    </div>
                    <button className="btn btn-danger" onClick={() => deleteExam(exam.id)}>
                      🗑️
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>

      {past.length > 0 && (
        <div className="card">
          <h3>📝 Provas Anteriores</h3>
          {past.map(exam => (
            <div key={exam.id} className="item" style={{ marginBottom: '10px', opacity: 0.6 }}>
              <div className="item-header">
                <div>
                  <h4>{exam.subject}</h4>
                  <small>{new Date(exam.exam_date).toLocaleDateString('pt-BR')}</small>
                </div>
                <button className="btn btn-danger" onClick={() => deleteExam(exam.id)}>
                  🗑️
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Exams;
