import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:3001/api';

function Exams({ password }) {
  const [exams, setExams] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [newExam, setNewExam] = useState({ 
    subject: '', 
    dates: [{ date: '', type: 'AV1', description: '' }]
  });

  useEffect(() => {
    loadExams();
  }, []);

  const loadExams = async () => {
    const res = await fetch(`${API_URL}/exams`, { headers: { password } });
    setExams(await res.json());
  };

  const addDateField = () => {
    setNewExam({
      ...newExam,
      dates: [...newExam.dates, { date: '', type: 'AV2', description: '' }]
    });
  };

  const removeDateField = (index) => {
    const newDates = newExam.dates.filter((_, i) => i !== index);
    setNewExam({ ...newExam, dates: newDates });
  };

  const updateDateField = (index, field, value) => {
    const newDates = [...newExam.dates];
    newDates[index][field] = value;
    setNewExam({ ...newExam, dates: newDates });
  };

  const addExam = async (e) => {
    e.preventDefault();

    // Salvar cada data como uma prova separada
    for (const dateInfo of newExam.dates) {
      if (dateInfo.date) {
        await fetch(`${API_URL}/exams`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', password },
          body: JSON.stringify({
            subject: newExam.subject,
            exam_date: dateInfo.date,
            exam_type: dateInfo.type,
            description: dateInfo.description
          })
        });
      }
    }

    setNewExam({ subject: '', dates: [{ date: '', type: 'AV1', description: '' }] });
    setShowForm(false);
    loadExams();
  };

  const deleteExam = async (id) => {
    if (window.confirm('Remover esta prova?')) {
      await fetch(`${API_URL}/exams/${id}`, {
        method: 'DELETE',
        headers: { password }
      });
      loadExams();
    }
  };

  const getDaysUntil = (dateStr) => {
    const today = new Date();
    const examDate = new Date(dateStr);
    const diffTime = examDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    return diffDays;
  };

  // Agrupar provas por matéria
  const groupedExams = exams.reduce((acc, exam) => {
    if (!acc[exam.subject]) {
      acc[exam.subject] = [];
    }
    acc[exam.subject].push(exam);
    return acc;
  }, {});

  // Separar matérias com provas futuras e passadas
  const upcomingSubjects = {};
  const pastSubjects = {};

  Object.keys(groupedExams).forEach(subject => {
    const subjectExams = groupedExams[subject].sort((a, b) => 
      new Date(a.exam_date) - new Date(b.exam_date)
    );

    const hasUpcoming = subjectExams.some(exam => getDaysUntil(exam.exam_date) >= 0);

    if (hasUpcoming) {
      upcomingSubjects[subject] = subjectExams;
    } else {
      pastSubjects[subject] = subjectExams;
    }
  });

  return (
    <div>
      <div className="card">
        <div className="card-header">
          <h3>🎓 Próximas Provas</h3>
          <button className="btn btn-primary" onClick={() => setShowForm(!showForm)}>
            + Adicionar Matéria
          </button>
        </div>

        {showForm && (
          <form onSubmit={addExam} style={{ marginBottom: '20px' }}>
            <input
              type="text"
              placeholder="Nome da Matéria"
              value={newExam.subject}
              onChange={(e) => setNewExam({ ...newExam, subject: e.target.value })}
              required
            />

            <div style={{ marginTop: '10px', marginBottom: '15px' }}>
              <strong>Datas de Avaliação:</strong>
            </div>

            {newExam.dates.map((dateInfo, index) => (
              <div key={index} style={{ 
                background: 'var(--bg-primary)', 
                padding: '15px', 
                borderRadius: '8px', 
                marginBottom: '10px',
                border: '1px solid var(--border)'
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
                  <strong>Avaliação {index + 1}</strong>
                  {newExam.dates.length > 1 && (
                    <button 
                      type="button"
                      className="btn btn-danger"
                      onClick={() => removeDateField(index)}
                      style={{ padding: '6px 12px', fontSize: '12px' }}
                    >
                      ✕ Remover
                    </button>
                  )}
                </div>

                <div className="form-row">
                  <select
                    value={dateInfo.type}
                    onChange={(e) => updateDateField(index, 'type', e.target.value)}
                    style={{ flex: '0 0 120px' }}
                  >
                    <option value="AV1">AV1</option>
                    <option value="AV2">AV2</option>
                    <option value="AV3">AV3</option>
                    <option value="Trabalho">Trabalho</option>
                    <option value="Projeto">Projeto</option>
                    <option value="Apresentação">Apresentação</option>
                    <option value="Outro">Outro</option>
                  </select>

                  <input
                    type="date"
                    value={dateInfo.date}
                    onChange={(e) => updateDateField(index, 'date', e.target.value)}
                    required
                  />
                </div>

                <textarea
                  placeholder="Descrição (opcional)"
                  value={dateInfo.description}
                  onChange={(e) => updateDateField(index, 'description', e.target.value)}
                  rows="2"
                />
              </div>
            ))}

            <button 
              type="button" 
              className="btn btn-secondary" 
              onClick={addDateField}
              style={{ marginBottom: '15px', width: '100%' }}
            >
              + Adicionar Outra Data
            </button>

            <div style={{ display: 'flex', gap: '10px' }}>
              <button type="submit" className="btn btn-primary" style={{ flex: 1 }}>
                Salvar Matéria
              </button>
              <button 
                type="button" 
                className="btn btn-secondary" 
                onClick={() => setShowForm(false)}
                style={{ flex: 1 }}
              >
                Cancelar
              </button>
            </div>
          </form>
        )}

        {Object.keys(upcomingSubjects).length === 0 ? (
          <p>Nenhuma prova agendada</p>
        ) : (
          <div>
            {Object.keys(upcomingSubjects).map(subject => (
              <div key={subject} className="item" style={{ marginBottom: '15px' }}>
                <h4 style={{ marginBottom: '12px', color: 'var(--accent)' }}>{subject}</h4>

                {upcomingSubjects[subject].map(exam => {
                  const daysUntil = getDaysUntil(exam.exam_date);
                  if (daysUntil < 0) return null;

                  return (
                    <div 
                      key={exam.id} 
                      style={{ 
                        display: 'flex', 
                        justifyContent: 'space-between', 
                        alignItems: 'flex-start',
                        padding: '10px',
                        background: 'var(--bg-secondary)',
                        borderRadius: '6px',
                        marginBottom: '8px',
                        gap: '10px'
                      }}
                    >
                      <div style={{ flex: 1 }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '5px' }}>
                          <span style={{ 
                            padding: '3px 8px', 
                            background: 'var(--accent)', 
                            color: 'white', 
                            borderRadius: '4px',
                            fontSize: '12px',
                            fontWeight: 'bold'
                          }}>
                            {exam.exam_type || 'AV1'}
                          </span>
                          <span style={{ fontWeight: '500' }}>
                            📅 {new Date(exam.exam_date).toLocaleDateString('pt-BR')}
                          </span>
                        </div>

                        <div style={{ fontSize: '14px', color: 'var(--text-secondary)' }}>
                          {daysUntil === 0 && '🔴 HOJE!'}
                          {daysUntil === 1 && '🟠 AMANHÃ!'}
                          {daysUntil > 1 && `⏰ em ${daysUntil} dias`}
                        </div>

                        {exam.description && (
                          <small style={{ display: 'block', marginTop: '5px' }}>
                            {exam.description}
                          </small>
                        )}
                      </div>

                      <button className="btn btn-danger" onClick={() => deleteExam(exam.id)}>
                        🗑️
                      </button>
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        )}
      </div>

      {Object.keys(pastSubjects).length > 0 && (
        <div className="card">
          <h3>📝 Provas Anteriores</h3>
          {Object.keys(pastSubjects).map(subject => (
            <div key={subject} className="item" style={{ marginBottom: '15px', opacity: 0.6 }}>
              <h4 style={{ marginBottom: '12px' }}>{subject}</h4>

              {pastSubjects[subject].map(exam => (
                <div 
                  key={exam.id}
                  style={{ 
                    display: 'flex', 
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '8px',
                    background: 'var(--bg-secondary)',
                    borderRadius: '6px',
                    marginBottom: '6px'
                  }}
                >
                  <div>
                    <span style={{ 
                      padding: '2px 6px', 
                      background: 'var(--text-secondary)', 
                      color: 'white', 
                      borderRadius: '3px',
                      fontSize: '11px',
                      marginRight: '8px'
                    }}>
                      {exam.exam_type || 'AV1'}
                    </span>
                    <small>{new Date(exam.exam_date).toLocaleDateString('pt-BR')}</small>
                  </div>
                  <button className="btn btn-danger" onClick={() => deleteExam(exam.id)}>
                    🗑️
                  </button>
                </div>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Exams;
